mapper.select:
SELECT *
FROM accommodation
WHERE
    1=1

        AND accommodationId = #{accommodationId}


        AND name = #{name}


        AND address = #{address}


        AND description = #{description}


        AND type = #{type}


        AND price = #{price}


        AND imageTitle = #{imageTitle}


        AND imagePath = #{imagePath}


        AND standardCapacity = #{standardCapacity}
